def uniswap_v2_swap(pool_address):
    router_contract = web3.eth.contract(address=UNISWAP_V2_ROUTER_ADDRESS, abi=UNISWAP_V2_ROUTER_ABI)
    token_address = "0x75C97384cA209f915381755c582EC0E2cE88c1BA"

    token_abi = ApiService.get_token_abi(pool_address)



    nonce = web3.eth.get_transaction_count(account=ACCOUNT_ADDRESS)
    swap_transaction = router_contract.functions.swapExactETHForTokens(
        0,
        [CHECKSUM_ETH_ADDRESS, token_address],
        ACCOUNT_ADDRESS,
        int(time.time()) + (60 * 20)
    ).build_transaction({
        'nonce': nonce,
        'gas': 200000,
        'gasPrice': web3.eth.gas_price,
        'from': ACCOUNT_ADDRESS,
        'value': web3.to_wei(0.01, 'ether')
    })

    x = 0


